        setTimeout(function () {
            toast.classList.remove(""show"");
        }, 3500);
    }

    function delaySubmit(form) {
        setTimeout(function () {
            form.submit();
        }, 1400);
    }

    window.onload = function () {
        var bookCards = document.querySelectorAll('.book-card');
